import React from "react";
import { Line,Bar,Pie } from 'react-chartjs-2';



export default class Chart extends React.Component{

    constructor(props){
        super(props);
        this.state={
            chartData:{
                labels: ['Boston', 'Worcester', 'Springfield', 'Lowell', 'Cambridge', 'New Bedford'],
                datasets:[
                  {
                    label:'Population',
                    data:[
                        617594,
                        181045,
                        153060,
                        106519,
                        105162,
                        95072,
                      ],
                    borderColor:"darkorange",
                    pointBackgroundColor:[
                        'rgba(255, 99, 132, 0.6)',
                        'rgba(54, 162, 235, 0.6)',
                        'rgba(255, 206, 86, 0.6)',
                        'rgba(75, 192, 192, 0.6)',
                        'rgba(153, 102, 255, 0.6)',
                        'rgba(255, 159, 64, 0.6)',
                        'rgba(255, 99, 132, 0.6)'
                    ]
                  }
                ]
              }
        }
    }

    componentDidMount(){

    }

    render(){
        return(
            <div>
            <div style={{backgroundColor:"white"}}>
                <Line
                    height="300px"
                    data={this.state.chartData}
                    options={{
                        maintainAspectRatio: false
                    }}
                />
            </div>
            <div className="row" style={{width:"100%", margin:"0px"}}>
                    <div className="col-4 col-xs-4 col-md-5 col-lg-5 col-sm-4" style={{backgroundColor:"white", margin:"1vw", left:"0px"}}>
                        <Bar
                            height="200px"
                            data={this.state.chartData}
                            options={{
                                maintainAspectRatio: false
                            }}
                        />
                    </div>
                    <div className="col-4 col-xs-4 col-md-5 col-lg-5 col-sm-4" style={{backgroundColor:"white", margin:"1vw", right:"0px"}}>
                        <Pie
                            height="200px"
                            data={this.state.chartData}
                            options={{
                                maintainAspectRatio: false
                            }}
                        />
                    </div>
            </div>
            </div>
        )
    }
}
