                10000000,
                10000000,
                10000000,
                10000000,
                ],

        }
    }


    render(){
        return(
            <div>
            <div style={{backgroundColor:"white", marginTop:"2vw"}}>
                <Line

                    height= {400}
                    data={dayChart(this.state.dataout)}
                    options={{
                        title:{
                            display: true,
                            text: "Line Chart", 
                            fontSize: 25
                        },
                        maintainAspectRatio: false
                    }}
                />
            </div>
            <div className="row" style={{width:"100%", margin:"0px", marginTop:"5vw"}}>
                    <Paper className="col-5" style={{backgroundColor:"white"}}>
                        <Bar
                            type="horizontalBar"
                            height={400}
                            data={monthChart(this.state.datamonth)}
                            options={{
                                title:{
                                    display: true,
                                    text: "Bar Chart", 
                                    fontSize: 25
                                },
                                maintainAspectRatio: false
                            }}
                        />
                    </Paper>
                    <div className="col-2">

                    </div>
                    <Paper className="col-5" style={{backgroundColor:"white"}}>
                        <Pie
                            height = {400}
                            data={yearChart(this.state.datayear)}
                            options={{
                                title:{
                                    display: true,
                                    text: "Pie Chart", 
                                    fontSize: 25
                                },
                                maintainAspectRatio: false
                            }}
                        />

                    </Paper>
            </div>
            </div>
        )
    }
}
