import React from 'react';
import ReactDOM from 'react-dom';
import './index.css';
import App from './App';
import * as serviceWorker from './serviceWorker';
import {createStore} from "redux";
import { Provider } from "react-redux"

const setcart = (state=[], action) =>{
    switch (action.type){
        case "add":
            if(state.length === 0){
                return Object.assign([], state, state.push({...action.cartitem, quantity: "1"}))
            }else{
            if(state.filter((item)=> {return item.productid === action.cartitem.productid}).length === 0){
                return Object.assign([], state, state.push({...action.cartitem, quantity: "1"}))
            }else{
                console.log(state.filter((item)=> {return item.productid === action.cartitem.productid}))
            }
        }
        break
        case "remove":
            return Object.assign([], state, state.filter((item) => item.id !== action.cartitem.id))
        default: return state
    }
}

const cartstore = createStore(setcart)
cartstore.subscribe(()=> console.log(cartstore.getState()))

ReactDOM.render(<Provider store={cartstore}>
                    <App />
                </Provider>, document.getElementById('root'));

// If you want your app to work offline and load faster, you can change
// unregister() to register() below. Note this comes with some pitfalls.
// Learn more about service workers: https://bit.ly/CRA-PWA
serviceWorker.unregister();
