import React from "react";
import { yellow } from "@material-ui/core/colors";

export default class Orders extends React.Component{

    constructor(props){
        super(props);
        this.state={
            orders:[]
        }
    }

    componentDidMount(){
        fetch("/orders")
        .then((res)=>res.json())
        .then(rows => this.setState({orders: rows}))
    }
    render(){
        return(
            <div>
                <h1>Orders List</h1>
                <table className="table">
                    <thead>
                        <tr>
                            <th>Order ID</th>
                            <th>Order Placer</th>
                            <th>Time Order</th>
                            <th>Total</th>
                            <th>Order Status</th>
                        </tr>
                    </thead>
                    <tbody>
                        {this.state.orders.map((order)=>
                            <tr key={order.orderid} style={{fontSize:"1.2vw"}}>
                                <th>{order.orderid}</th>
                                <th>{order.name}</th>
                                <th>{order.timeorder}</th>
                                <th>{order.total}</th>
                                <th style={{color:(order.orderstatus === "done") ? "green" : (order.orderstatus === "cancel") ? "red" : "yellow"}}>{order.orderstatus}</th>
                            </tr>
                        )}
                    </tbody>
                </table>
            </div>
        )
    }
}
