import React from "react";
import { connect } from "react-redux"
import CancelIcon from '@material-ui/icons/Cancel';
import { Fab } from "@material-ui/core";

const mapStateToProps = (state) =>{
    return { productincart : state}
}



class CartPage extends React.Component{
    constructor(props){
        super(props);
        this.state ={
            total: 0
        }
    }

    componentDidMount(){
        this.totalcount(0,0)
    }

    totalcount = (total,i)=>{
        if(i<this.props.productincart.length){
            const sumtotal = total + (this.props.productincart[i].price*this.props.productincart[i].quantity)
                const i2 = i+1
                this.totalcount(sumtotal, i2)
        }else{
            this.setState({total: total})
        }
    }

    del = (cartitem, total, i) =>{
        Promise.resolve(this.props.dispatch({type:"remove", cartitem}))
        .then(()=>{
        this.totalcount(0,0)
    }
        )
    }

    render(){
        console.log(this.props.productincart.length)
    return(
            <div className=" bodycartpage">
                <div className="row" style={{paddingTop: "40px", paddingBottom:"20px", position:"relative", left:"-40px", minHeight: "500px" }}>
                    <div className="col-8 col-md-8 col-sm-8 col-xs-8" style={{top:"-14px"}} >
                            {this.props.productincart.map(cartitem => 
                            <div key={cartitem.productid} className="card" style={{ width: "95%"}}>
                                <div className="row no-gutters">
                                    <div className="col-3 col-md-3 col-sm-3 col-xs-3">
                                        <img height="100%" width="100%" style={{position:'relative'}} src={cartitem.image} alt="testproductimg"/>
                                    </div>
                                    <div className="col-9 col-md-9 col-sm-9 col-xs-9" style={{border:"1px solid darkorange", borderLeft:"0px"}}>
                                        <Fab size="small" variant="extended" onClick={()=>{Promise.resolve(this.props.dispatch({type: "remove", cartitem})).then(()=>{this.totalcount(0,0)})}} style={{background: "red", width:"20px", height:"20px" ,position:"absolute", minWidth:"auto", padding:"1px", right:"-3%", top:"-8%"}}>
                                                    <CancelIcon style={{height:"18px", width:"18px"}} />
                                        </Fab>
                                        <table className="cashout table table-sm table-borderless" style={{color:"darkorange",height:"80%" ,width: "101%", left:"0px",top:"5%", fontSize: "20px", fontFamily:"monospace"}}>
                                            <tbody>
                                                <tr>
                                                    <td style={{textAlign:"left", fontSize:"80%"}}>{cartitem.productname}</td>
                                                    <td style={{textAlign:"center", right:"7%",fontSize:"80%" ,position:"absolute"}}>Số Lượng</td>
                                                </tr>
                                                <tr>
                                                    <td style={{textAlign:"left", fontSize:"80%",top:"60%" ,position:"absolute" ,width:"45%"}}>{cartitem.price}</td>
                                                    <td>
                                                        <div className="input-group input-group-sm mb-3" style={{right: "7%", top:"60%" ,width:"40%", maxWidth:"85px",position: "absolute"}} >
                                                            <div className="input-group-prepend">
                                                            <button className="btn btn-outline-primary btn-sm" disabled={cartitem.quantity===1} onClick={()=>{this.props.dispatch({type:"-quantity", cartitem}); this.totalcount(0,0)}}>-</button>
                                                            </div>
                                                            <input className="form-control" type="text" disabled style={{width:"10%",fontFamily: "sans-serif" ,textAlign:"center"}} value={cartitem.quantity}></input>
                                                            <div className="input-group-append">
                                                            <button className="btn btn-outline-primary btn-sm" disabled={cartitem.quantity===20} onClick={()=>{this.props.dispatch({type:"add", cartitem}); this.totalcount(0,0)}}>+</button>
                                                            </div>
                                                        </div>
                                                    </td>
                                                </tr>
                                            </tbody>
                                        </table>
                                    </div>
                                </div>
                            </div>
                            )}
                    </div>
                    <div className="col-4 col-sm-4 col-xs-4 col-md-4" style={{borderLeft: "1px solid darkorange", position:"sticky", maxHeight:"500px", top:"10px"}}>
                        <div className="cashout card" style={{background:"transparent",fontFamily:"monospace",left:"-20px" ,maxWidth: "100%",color:"darkorange", maxHeight:"400px", minHeight:"100px"}}>
                            <div className="cashout cardheader" style={{textAlign: "center", fontSize: "25px",height:"40px"}}>Total</div>
                            <table className="cashout cardbody table table-borderless" style={{ top:"80px",color:"darkorange" ,fontSize:"18px"}}>
                                <tbody>
                                    <tr style={{borderBottom:"1px solid darkorange"}}>
                                        <td style={{ textAlign:"left"}}>Total Price</td>
                                        <td style={{ textAlign:"right"}}>{this.state.total+"  VND"}</td>
                                    </tr>
                                    <tr style={{borderBottom:"1px solid darkorange"}}>
                                        <td style={{ textAlign:"left"}}>Discount</td>
                                        <td style={{ textAlign:"right"}}>0</td>
                                    </tr>
                                    <tr style={{borderBottom:"1px solid darkorange"}}>
                                        <td style={{ textAlign:"left"}}>Total Pay</td>
                                        <td style={{ textAlign:"right"}}>{this.state.total+"  VND"}</td>
                                    </tr>
                                </tbody>
                            </table>
                        </div>
\                        <img  style={{top: "390px", left:"-10px", height:"5%", position:"relative"}} src="../../../image/creditcard.png" width="40%" alt="creditcard"/>
                        <button className=" btn btn-danger" style={{top: "390px", width:"90%", maxHeight:"38px", position:"relative"}}>Thanh Toan</button>
                    </div>
                </div>
                <div    className="alert alert-success" role="alert">
                                sadasdas
                </div>
            </div>
    )
    }
}

export default connect(mapStateToProps)(CartPage)
