import React from "react";
import { Line,Bar,Pie } from 'react-chartjs-2';
import Paper from '@material-ui/core/Paper';


export default class Chart extends React.Component{

    componentDidMount(){

    }
    dataout=[
        61759412,
        48104523,
        35306067,
        20651951,
        60516298,
        95072191,
        58900421,
        52234142,
        82345382,
        99281328,
        67921451,
        82341376
      ]
    chartData={
        labels: ['Tháng 1', 'Tháng 2', 'Tháng 3', 'Tháng 4', 'Tháng 5', 'Tháng 6', "Tháng 7", "Tháng 8", "Tháng 9", "Tháng 10", "Tháng 11", "Tháng 12"],
        datasets:[
          {
            label:'Tổng Doanh Thu',
            data:this.dataout,
            borderColor:"darkorange",
            fill:"white",
            backgroundColor:[
                'rgba(255, 99, 132, 0.6)',
                'rgba(229, 241, 75, 0.6)',
                'rgba(56, 89, 236, 0.6)',
                'rgba(75, 192, 192, 0.6)',
                'rgba(247, 52, 52, 0.6)',
                'rgba(113, 220, 95, 0.6)',
                'rgba(255, 99, 132, 0.6)',
                'rgba(150, 100, 53, 0.6)',
                'rgba(209, 45, 123, 0.6)',
                'rgba(184, 200, 100, 0.6)',
                'rgba(168, 178, 219, 0.6)',
                'rgba(198, 25, 90, 0.6)',
                'rgba(250, 0, 0, 0.6)'
            ]
          }
        ]
      }
    render(){
        return(
            <div>
            <div style={{backgroundColor:"white", marginTop:"2vw"}}>
                <Line

                    height= {400}
                    data={this.chartData}
                    options={{
                        title:{
                            display: true,
                            text: "Line Chart", 
                            fontSize: 25
                        },
                        maintainAspectRatio: false
                    }}
                />
            </div>
            <div className="row" style={{width:"100%", margin:"0px", marginTop:"5vw"}}>
                    <Paper className="col-5" style={{backgroundColor:"white"}}>
                        <Bar
                            type="horizontalBar"
                            height={400}
                            data={this.chartData}
                            options={{
                                title:{
                                    display: true,
                                    text: "Bar Chart", 
                                    fontSize: 25
                                },
                                maintainAspectRatio: false
                            }}
                        />
                    </Paper>
                    <div className="col-2">

                    </div>
                    <Paper className="col-5" style={{backgroundColor:"white"}}>
                        <Pie
                            height = {400}
                            data={this.chartData}
                            options={{
                                title:{
                                    display: true,
                                    text: "Pie Chart", 
                                    fontSize: 25
                                },
                                maintainAspectRatio: false
                            }}
                        />

                    </Paper>
            </div>
            </div>
        )
    }
}
