import React from "react";
import {Link} from 'react-router-dom';

export default class Orders extends React.Component{

    constructor(props){
        super(props);
        this.state={
            orders:[]
        }
    }

    componentDidMount(){
        fetch("/orders")
        .then((res)=>res.json())
        .then(rows => this.setState({orders: rows}))
    }
    // handleDoneBtn=()=>{
    //     if(window.confirm("Are you want to complete this order ?"){
    //         fetch("/")
    //     }
    // }
    render(){
        return(
            <div>
                <h1>Orders List</h1>
                <table className="table">
                    <thead>
                        <tr style={{color:"white", fontSize:"1.2vw"}}>
                            <th>Order ID</th>
                            <th>Order Placer</th>
                            <th>Time Order</th>
                            <th>Total</th>
                            <th>Order Status</th>
                            <th>Action</th>
                        </tr>
                    </thead>
                    <tbody>
                        {this.state.orders.map((order)=>
                            <tr key={order.orderid} style={{textAlign:"left",fontSize:"1.2vw", color:(order.orderstatus === "Done") ? "green" : (order.orderstatus === "Cancel") ? "red" : "yellow"}}>
                                <th><Link style={{color:"currentColor"}} to={"/admin/orderdetail/"+order.orderid}>{order.orderid}</Link></th>
                                <th>{order.name}</th>
                                <th>{order.timeorder}</th>
                                <th>{order.total+" VND"}</th>
                                <th>{order.orderstatus}</th>
                                {(order.orderstatus === "Undone") ? <th><button className="btn btn-sm btn-xs btn-md btn-success" onClick={(e)=>console.log(e.target.value)} value="Done">Done</button></th> : <th></th>}
                            </tr>
                        )}
                    </tbody>
                </table>
            </div>
        )
    }
}
