import React from "react";
import Cart from "./cartPageSteper/Cart";
import UserOrderInfo from "./cartPageSteper/UserOrderInfo"
import { makeStyles } from '@material-ui/core/styles';
import Stepper from '@material-ui/core/Stepper';
import Step from '@material-ui/core/Step';
import StepLabel from '@material-ui/core/StepLabel';
import Button from '@material-ui/core/Button';
import Typography from '@material-ui/core/Typography';
import CompleteOrder from "./cartPageSteper/CompleteOrder"

const useStyles = makeStyles(theme => ({
    root: {
      width: '90%',
      backgroundColor:"black"
    },
    backButton: {
      marginRight: theme.spacing(1),
      minWidth: "100px"
    },
    instructions: {
      marginTop: theme.spacing(1),
      marginBottom: theme.spacing(1),

    },
  }));

  function getSteps() {
    return ['Choose Your Food', 'Give Us Your Info', 'Confirm your order'];
  }

function ConfirmEmail(props) {
    const [email, setEmail] = React.useState("")
    return(
        <div>
            <label>Bạn hãy nhập Email của bạn </label>
            <input></input>
            <button onClick={props.next}>Next</button>
        </div>
    )
}

function ConfirmUsername(props){
    const [username, setUsername] = React.useState("")
    return(
        <div>
            <label>Bạn hãy nhập username của bạn</label>
            <input></input>
            <button onClick={props.next}>Next</button>
        </div>
    )
}

function ChangePass(props){
    const [oldPass, setOldPass] = React.useState("")
    const [newPass, setNewPass] = React.useState("")
    const [confNewPass, setConfNewPass] = React.useState("")
    return(
        <div>
            <label>Bạn hãy nhập mật khẩu cũ</label>
            <input value={oldPass} onChange={(e)=>setOldPass(e.target.value)}></input>
            <label>Bạn hãy nhập mật khẩu mới</label>
            <input value={newPass} onChange={(e)=>setNewPass(e.target.value)}></input>
            <label>Xác nhận mật khẩu mới</label>
            <input value={confNewPass} onChange={(e)=>setConfNewPass(e.target.value)}></input>
            <button onClick={props.next}>Finish</button>
        </div>
    )
}
function completeChange(props){
    return(
        <div>
            <button className="btn btn-outline-danger">Quay Trở lại trang đăng nhập</button>
        </div>
    )
}

export default function PasswordChange(props){
    const classes = useStyles();
    const [activeStep, setActiveStep] = React.useState(0);
    const steps = getSteps();


    const getStepContent=(stepIndex)=>{
        switch (stepIndex) {
          case 0:
            return <ConfirmEmail next={handleNext}/>;
          case 1:
            return <ConfirmUsername next={handleNext}/>;
          case 2:
            return <CompleteOrder />;
          default:
            return 'Unknown stepIndex';
        }
    }

    const handleNext = () => {
        setActiveStep(prevActiveStep => prevActiveStep + 1);
    };

    const handleBack = () => {
        setActiveStep(prevActiveStep => prevActiveStep - 1);
    };

    const handleReset = () => {
        setActiveStep(0);
    };

            return(
                <div className={classes.root}>
                <Stepper activeStep={activeStep} alternativeLabel>
                  {steps.map(label => (
                    <Step key={label}>
                      <StepLabel>{label}</StepLabel>
                    </Step>
                  ))}
                </Stepper>
                <div>
                  {activeStep === steps.length ? (
                    <div>
                      <Typography className={classes.instructions}>All steps completed</Typography>
                      <Button onClick={handleReset}>Reset</Button>
                    </div>
                  ) : (
                    <div  style={{marginLeft: "150px", marginRight:"150px"}}>
                      <Typography className={classes.instructions}></Typography>
                      <div>
                        <Button
                          disabled={activeStep !== 1}
                          onClick={handleBack}
                          className={classes.backButton}
                          variant="outlined"
                          color ="secondary"
                        >
                          Back
                        </Button>
                      </div>
                    </div>
                  )}
                </div>
                {getStepContent(activeStep)}
              </div>
        )
}
