import React from "react";
import { Line,Bar,Pie } from 'react-chartjs-2';
import Paper from '@material-ui/core/Paper';


export default class Chart extends React.Component{

    componentDidMount(){

    }
    chartData={
        labels: ['Boston', 'Worcester', 'Springfield', 'Lowell', 'Cambridge', 'New Bedford'],
        datasets:[
          {
            label:'Population',
            data:[
                617594,
                181045,
                153060,
                106519,
                105162,
                95072,
              ],
            borderColor:"darkorange",
            fill:"white",
            backgroundColor:[
                'rgba(255, 99, 132, 0.6)',
                'rgba(229, 241, 75, 0.6)',
                'rgba(56, 89, 236, 0.6)',
                'rgba(75, 192, 192, 0.6)',
                'rgba(247, 52, 52, 0.6)',
                'rgba(113, 220, 95, 0.6)',
                'rgba(255, 99, 132, 0.6)'
            ]
          }
        ]
      }
    render(){
        return(
            <div>
            <div style={{backgroundColor:"white"}}>
                <Line
                    onElementsClick={console.log((e)=>e.target)}
                    height= {400}
                    data={this.chartData}
                    options={{
                        title:{
                            display: true,
                            text: "Line Chart", 
                            fontSize: "3vw"
                        },
                        maintainAspectRatio: false
                    }}
                />
            </div>
            <div className="row" style={{width:"100%", margin:"0px", marginTop:"5vw"}}>
                    <Paper className="col-5" style={{backgroundColor:"white"}}>
                        <Bar
                            type="horizontalBar"
                            height={300}
                            data={this.chartData}
                            options={{
                                title:{
                                    display: true,
                                    text: "Bar Chart", 
                                    fontSize: "3vw"
                                },
                                maintainAspectRatio: false
                            }}
                        />
                    </Paper>
                    <div className="col-2">

                    </div>
                    <Paper className="col-5" style={{backgroundColor:"white"}}>
                        <Pie
                            height={300}
                            data={this.chartData}
                            options={{
                                title:{
                                    display: true,
                                    text: "Pie Chart", 
                                    fontSize: "3vw"
                                },
                                maintainAspectRatio: false
                            }}
                        />

                    </Paper>
            </div>
            </div>
        )
    }
}
