import React from 'react';

class About extends React.Component {


  constructor(props){
    super(props)

    this.nameOrder = React.useRef();
    this.phoneNumber = React.useRef();
    this.email = React.useRef();
    this.address = React.useRef();
    this.addressWard = React.useRef();
    this.addressDistrict = React.useRef();
}

Validate =()=>{
    console.log()
}
render(){
return(
    <div className="userorderinfo needs-validation" noValidate style={{position:"relative", textAlign:"center" }}>
        <div className="form-group">
            <label style={{color:"darkorange"}}>Họ Và Tên</label>
            <input className="form-control" type="text" ></input>
        </div>
        <div className="form-group">
            <label style={{color:"darkorange"}}>Số Điện Thoại</label>
            <input className="form-control" type="text" ></input>
        </div>
        <div className="form-group">
            <label style={{color:"darkorange"}}>Email</label>
            <input className="form-control" type="text" ></input>
        </div>
        <div className="form-group">
            <label style={{color:"darkorange"}}>Địa Chỉ</label>
            <input className="form-control" type="text" ></input>
            <br/>
            <div className="row">
                <div className="col">
                    <select className="form-control" style={{padding:"0px"}} >
                        <option>Phường 1</option>
                    </select>
                </div>
                <div className="col">
                    <select className="form-control" style={{padding:"0px"}} >
                        <option>Quận 1</option>
                    </select>
                </div>
            </div>
        </div>
        <button className="btn btn-primary"  onClick={this.Validate}>Xác Nhận</button>
    </div>
)
}

}

export default About;
