import React from 'react';
import CheckCircleOutlineIcon from '@material-ui/icons/CheckCircleOutline';


class About extends React.Component {

  constructor(props){
    super(props);

    this.nameOrder = React.createRef();
    this.phoneNumber = React.createRef();
    this.email = React.createRef();
    this.address = React.createRef();
    this.addressWard = React.createRef();
    this.addressDistrict = React.createRef();
    this.state={
      validName: true,
      validPhone:true,
      validEmail:true,
      validAddress:true,
    }
}

// nameOrder = this.nameOrder.current.value

Validate =()=>{
    if(this.nameOrder.current.value == ""){
      this.setState({validName: false})
    }
}
render(){
return(
    <div className="userorderinfo needs-validation" noValidate style={{position:"relative", textAlign:"center" }}>
        <div className="form-group">
            <label style={{color:"darkorange"}}>Họ Và Tên</label>
            <input className="form-control" ref={this.nameOrder} type="text" ></input>
            {(this.state.validName === false) ? <div style={{textAlign:"left", color:"red"}}>Vui long nhap ho ten !!!</div> : <div><CheckCircleOutlineIcon /></div>}
        </div>
        <div className="form-group">
            <label style={{color:"darkorange"}}>Số Điện Thoại</label>
            <input className="form-control" ref={this.phoneNumber} type="text" ></input>
        </div>
        <div className="form-group">
            <label style={{color:"darkorange"}}>Email</label>
            <input className="form-control" ref={this.email} type="text" ></input>
        </div>
        <div className="form-group">
            <label style={{color:"darkorange"}}>Địa Chỉ</label>
            <input className="form-control" ref={this.address} type="text" ></input>
            <br/>
            <div className="row">
                <div className="col">
                    <select className="form-control" ref={this.addressWard} style={{padding:"0px"}} >
                        <option>Phường 1</option>
                    </select>
                </div>
                <div className="col">
                    <select className="form-control" ref={this.addressDistrict} style={{padding:"0px"}} >
                        <option>Quận 1</option>
                    </select>
                </div>
            </div>
        </div>
        <button className="btn btn-primary"  onClick={this.Validate}>Xác Nhận</button>
    </div>
)
}

}

export default About;
