import React from "react";
import { connect } from "react-redux"
import CancelIcon from '@material-ui/icons/Cancel';
import { Fab } from "@material-ui/core";

const mapStateToProps = (state) =>{
    return { productincart : state}
}



class Cart extends React.Component{
    constructor(props){
        super(props);
        this.state ={
            total: 0
        }
    }

    componentDidMount(){
        this.totalcount(0,0)
    }

    totalcount = (total,i)=>{
        if(i<this.props.productincart.length){
            const sumtotal = total + (this.props.productincart[i].price*this.props.productincart[i].quantity)
                const i2 = i+1
                this.totalcount(sumtotal, i2)
        }else{
            this.setState({total: total})
        }
    }

    del = (cartitem, total, i) =>{
        Promise.resolve(this.props.dispatch({type:"remove", cartitem}))
        .then(()=>{
        this.totalcount(0,0)
    }
        )
    }

    render(){
        console.log(this.props.productincart.length)
    return(
            <div className=" bodycartpage">
                <div className="row" style={{paddingTop: "40px", paddingBottom:"20px", position:"relative", left:"0px", minHeight: "500px" }}>
                    <div className="col-6 col-md-8 col-sm-7 col-xs-6" style={{top:"-14px"}} >
                            {this.props.productincart.map(cartitem => 
                            <div key={cartitem.productid} className="card" style={{ width: "95%"}}>
                                <div className="row no-gutters">
                                    <div className="col-6 col-md-3 col-sm-4 col-xs-5">
                                        <img height="100%" width="100%" style={{position:'relative'}} src={cartitem.image} alt="testproductimg"/>
                                    </div>
                                    <div className="col-6 col-md-9 col-sm-8 col-xs-7" style={{border:"1px solid darkorange", borderLeft:"0px"}}>
                                        <Fab size="small" variant="extended" onClick={()=>{Promise.resolve(this.props.dispatch({type: "remove", cartitem})).then(()=>{this.totalcount(0,0)}).then(()=>{alert("sdsd")})}} style={{background: "red", width:"2vw", height:"2vw" ,position:"absolute", minWidth:"auto", padding:"1px", right:"-3%", top:"-8%"}}>
                                                    <CancelIcon style={{height:"1.7vw", width:"1.7vw"}} />
                                        </Fab>
                                        <table className="cashout table table-md table-xs table-sm table-borderless" style={{color:"darkorange",height:"80%" ,width: "101%", left:"0px",top:"5%" ,fontSize: "20px", fontFamily:"monospace"}}>
                                            <tbody>
                                                <tr>
                                                    <td style={{textAlign:"left", fontSize:"1.2vw"}}>{cartitem.productname}</td>
                                                    <td style={{textAlign:"center", right:"7%",fontSize:"1.2vw" ,position:"absolute"}}>Số Lượng</td>
                                                </tr>
                                                <tr>
                                                    <td style={{textAlign:"left", fontSize:"1.2vw",top:"60%" ,position:"absolute" ,width:"45%"}}>{cartitem.price}</td>
                                                    <td>
                                                        <div className="input-group input-group-sm mb-3" style={{right: "7%", top:"60%" ,width:"40%", maxWidth:"85px",position: "absolute"}} >
                                                            <div className="input-group-prepend">
                                                            <button className="btn btn-outline-primary btn-sm" style={{height:"2vw", fontSize:"1vw", width:"2vw"}} disabled={cartitem.quantity===1} onClick={()=>{this.props.dispatch({type:"-quantity", cartitem}); this.totalcount(0,0)}}>-</button>
                                                            </div>
                                                            <input className="form-control-sm" type="text" disabled style={{width:"2vw",height:"2vw", fontSize:"1vw", padding:"0px",fontFamily: "sans-serif" ,textAlign:"center"}} value={cartitem.quantity}></input>
                                                            <div className="input-group-append">
                                                            <button className="btn btn-outline-primary btn-sm" style={{height:"2vw", fontSize:"1vw", width:"2vw"}} disabled={cartitem.quantity===20} onClick={()=>{this.props.dispatch({type:"add", cartitem}); this.totalcount(0,0)}}>+</button>
                                                            </div>
                                                        </div>
                                                    </td>
                                                </tr>
                                            </tbody>
                                        </table>
                                    </div>
                                </div>
                            </div>
                            )}
                    </div>
                    <div className="col-6 col-sm-5 col-xs-6 col-md-4" style={{borderLeft: "1px solid darkorange", position:"sticky", maxHeight:"500px", top:"10px"}}>
                        <div className="cashout card" style={{background:"transparent",fontFamily:"monospace",left:"-20px" ,maxWidth: "100%",color:"darkorange", maxHeight:"400px", minHeight:"100px"}}>
                            <div className="cashout cardheader" style={{textAlign: "center", fontSize: "2.6vw",height:"40px"}}>Total</div>
                            <table className="cashout cardbody table table-borderless" style={{ top:"80px",color:"darkorange" ,fontSize:"1.3vw"}}>
                                <tbody>
                                    <tr style={{borderBottom:"1px solid darkorange"}}>
                                        <td style={{ textAlign:"left"}}>Total Price</td>
                                        <td style={{ textAlign:"right"}}>{this.state.total+"  VND"}</td>
                                    </tr>
                                    <tr style={{borderBottom:"1px solid darkorange"}}>
                                        <td style={{ textAlign:"left"}}>Discount</td>
                                        <td style={{ textAlign:"right"}}>0</td>
                                    </tr>
                                    <tr style={{borderBottom:"1px solid darkorange"}}>
                                        <td style={{ textAlign:"left"}}>Total Pay</td>
                                        <td style={{ textAlign:"right"}}>{this.state.total+"  VND"}</td>
                                    </tr>
                                </tbody>
                            </table>
                        </div>
                     <img  style={{top: "380px", left:"15px", height:"5%", position:"relative"}} src="../../../image/creditcard.png" width="40%" alt="creditcard"/>
                        <button className=" btn btn-danger" style={{fontSize:"1.2vw",top: "380px", left:"15px", width:"90%", maxHeight:"38px", position:"relative"}} onClick={this.props.next}>Thanh Toan</button>
                    </div>
                </div>
            </div>
    )
    }
}

export default connect(mapStateToProps)(Cart)
