import React from 'react';
import './Adding.css';
import ErrorIcon from '@material-ui/icons/Error';

class UpdateUser extends React.Component{

    constructor(props){
        super(props);
        this.state={
            username: "",
            password: "",
            hoten: "",
            email: "",
            role: "",
            usernameError: "",
            passwordError: "",
            hotenError: "",
            emailError: "",
            roleError:""
        }
    }

    updateid = this.props.match.params.id

    componentDidMount(){
        fetch('/update-user/'+this.updateid)
        .then((res) => res.json())
        .then(rows => this.setState({updateitem: rows[0]}))
    }

    changeInput=(e)=>{
        this.setState({[e.target.name]: e.target.value})
    }



    render(){
        console.log(this.state.updateitem);
        return(
            <form action={'/updating-user/'+this.updateid} method='Post'>
                <h1>UpdateUser</h1>
                <div className='form-group'>
                    <label>Username</label>
                    <input name='username' className="form-control" type='text' value={this.state.username} onChange={(e)=>this.changeInput(e)}></input>
                </div>
                <div className='form-group'>
                    <label>Password</label>
                    <input name='password' className="form-control" type='text' value={this.state.password} onChange={(e)=>this.changeInput(e)}></input>
                </div>
                <div className='form-group'>
                    <label>Hoten</label>
                    <input name='hoten' className="form-control" type='text' value={this.state.hoten} onChange={(e)=>this.changeInput(e)}></input>
                </div>
                <div className='form-group'>
                    <label>Email</label>
                    <input name='email' className="form-control" type='text' value={this.state.email} onChange={(e)=>this.changeInput(e)}></input>
                </div>
                <div className="form-group">
                    <label style={{fontSize:"1.3vw", color:"white"}}>Vai trò</label>
                    <select name="role" className="form-control" value={this.state.role} onChange={(e)=>this.changeInput(e)}>
                        <option></option>
                        <option>superadmin</option>
                        <option>admin</option>
                    </select>
                    <div style={{textAlign:"left", color:"red", fontSize:"1.2vw"}}>
                          {(this.state.roleError) ? <ErrorIcon /> : null}
                          {this.state.roleError}
                    </div>
                </div>
                <div className='form-group'>
                    <button className='btn btn-danger' type='reset'>Reset</button>
                    <button className='btn btn-primary' type='submit'>Update</button>
                </div>
            </form>
        )
    }
}

export default UpdateUser;
