import React from 'react';
import './account.css';
import tphcm from '../../tphcm';
import ErrorIcon from '@material-ui/icons/Error';

class SignUp extends React.Component{

    constructor(props){
        super(props);
        this.state={
            firstname: "",
            lastname: "",
            gender: "",
            phone: "",
            address: "",
            email: "",
            username: "",
            password: "",
            passwordconf: "",
            errorfirstname: "",
            errorlastname: "",
            errorgender: "",
            errorphone: "",
            erroraddress: "",
            erroremail: "",
            errorusername: "",
            errorpassword: "",
            errorpasswordconf: "",
        }
    }

    handleOnChange = (e) =>{
        this.setState({[e.target.name] : e.target.value})
    }
    validate = () =>{
        if(this.state.firstname === ""){
            this.setState({errorfirstname: "Vui lòng không bỏ trống !"})
        }
        if(this.state.lastname === ""){
            this.setState({errorlastname: "Vui lòng không bỏ trống !"})
        }
        if(this.state.gender === ""){
            this.setState({errorgender: "Vui lòng không bỏ trống !"})
        }
        if(this.state.phone === ""){
            this.setState({errorphone: "Vui lòng không bỏ trống !"})
        }else{
            if(isNaN(this.state.orderPhone) ||  this.state.orderPhone.split("")[0] !== "0" || this.state.orderPhone.length !== 10){
                this.setState({errorphone: "Số điện thoại không hợp lệ !"})
            }
          }
        if(this.state.address === ""){
            this.setState({erroraddress: "Vui lòng không bỏ trống !"})
        }
        if(this.state.email === ""){
            this.setState({erroremail: "Vui lòng không bỏ trống !"})
        }else{
            if(!this.state.orderEmail.includes("@gmail.com")){
                this.setState({erroremail: "Email không hợp lệ ! Vui lòng sử dụng địa chỉ gmail"})
            }
          }
        if(this.state.username === ""){
            this.setState({errorusername: "Vui lòng không bỏ trống !"})
        }
        if(this.state.password === ""){
            this.setState({errorpassword: "Vui lòng không bỏ trống !"})
        }
        if(this.state.passwordconf === ""){
            this.setState({errorpasswordconf: "Vui lòng không bỏ trống !"})
        }else{
            if(this.state.passwordconf !== this.state.password){
                this.setState({errorpasswordconf: "Mật khẩu xác nhận không giống !"})
            }
        }
    }
    handleSubmit = () =>{
        fetch('/add-user', {
            method:"post",
            headers:{'Content-Type':'application/json'},
            body: JSON.stringify({
                "firstname": this.state.firstname,
                "lastname": this.state.lastname,
                "gender": this.state.gender,
                "phone": this.state.phone,
                "address": this.state.address,
                "email": this.state.email,
                "username": this.state.username,
                "password": this.state.password
            })
        })
    }

    render(){
        return(
            <div className='SignUp'>
                <h1 style={{color:'darkorange'}}>Sign Up Member</h1>
            <div action='/add-customer' method='post' style={{backgroundColor: 'black'}}>

                <div className="form-row">
                    <div className="form-group col-6">
                    <label style={{color: 'white'}}>First Name</label>
                    <input name='firstname' type="text" className="form-control" value={this.state.firstname} onChange={(e)=>this.handleOnChange(e)} placeholder="First Name..."/>
                    <div style={{textAlign:"left",fontSize:"1vw" ,color:"red"}}>Input at least 6 characters</div>
                    </div>
                    <div className="form-group col-6">
                    <label style={{color: 'white'}}>Last Name</label>
                    <input name='lastname' type="text" className="form-control" value={this.state.lastname} onChange={(e)=>this.handleOnChange(e)} placeholder="Last Name..."/>
                    <div style={{textAlign:"left",fontSize:"1vw" ,color:"red"}}>Input at least 6 characters</div>
                    </div>
                </div>
                <div className="form-row">
                    <div className="form-group col-3">
                        <label style={{color: 'white'}}>Gender</label>
                        <select name='gender' value={this.state.gender} onChange={(e)=>this.handleOnChange(e)} className="form-control">
                            <option></option>
                            <option value='Male' defaultValue>Male</option>
                            <option value='Female' >Female</option>
                        </select>
                    </div>
                    <div className='col-3'>

                    </div>
                    <div className='form-group col-6'>
                        <label style={{color: 'white'}}>Phone Number</label>
                        <input name='phone' type="text" className="form-control" value={this.state.phone} onChange={(e)=>this.handleOnChange(e)} placeholder="Phone Number..."></input>
                    </div>

                </div>
                <div className='form-group'>
                    <label style={{color: 'white'}}>Address</label>
                    <input name='address' type="text" className="form-control" value={this.state.address} onChange={(e)=>this.handleOnChange(e)} placeholder="Address..."></input>
                </div>
                <div className="row">
                    <div className="col">
                        <select name="orderAddressDistrict" className="form-control" style={{padding:"0px"}} value={this.state.orderAddressDistrict} onChange={(e)=>this.hanldeInput(e)}>
                            <option></option>
                            {tphcm.map((district, index) => 
                                <option key={index}>{district.name}</option>
                                )}
                        </select>
                        <div style={{textAlign:"right", color:"red", fontSize:"1.2vw"}}>
                          {(this.state.orderAddressDistrictError) ? <ErrorIcon /> : null}
                          {this.state.orderAddressDistrictError}
                        </div>
                    </div>
                    <div className="col">
                        <select name="orderAddressWard" className="form-control" style={{padding:"0px"}} value={this.state.orderAddressWard} onChange={(e)=>this.hanldeInput(e)}>
                            <option></option>
                            {
                            (this.state.orderAddressDistrict === "") ? null 
                            : tphcm.find((district)=>{return district.name === this.state.orderAddressDistrict}).district.map((ward, index)=><option key={index}>{ward}</option>)
                            }
                        </select>
                        <div style={{textAlign:"right", color:"red", fontSize:"1.2vw"}}>
                          {(this.state.orderAddressWardError) ? <ErrorIcon /> : null}
                          {this.state.orderAddressWardError}
                        </div>
                    </div>
                </div>
                <div className='form-group'>
                    <label style={{color: 'white'}}>Email</label>
                    <input name='email' type="text" className="form-control" value={this.state.email} onChange={(e)=>this.handleOnChange(e)} placeholder="Email..."></input>
                </div>
                <div className='form-group'>
                    <label style={{color: 'white'}}>UserName</label>
                    <input name='username' type="text" className="form-control" value={this.state.username} onChange={(e)=>this.handleOnChange(e)} placeholder="UserName..."></input>
                </div>
                <div className='form-group'>
                    <label style={{color: 'white'}}>Password</label>
                    <input name='password' type="password" className="form-control" value={this.state.password} onChange={(e)=>this.handleOnChange(e)} placeholder="Password..."></input>
                    <small style={{color: 'white'}} className="form-text text-muted">
                    Your password must be 8-20 characters long and must not contain spaces, special characters, or emoji.
                    </small>
                </div>
                <div className='form-group'>
                    <label style={{color: 'white'}}>Confirm Password</label>
                    <input type="confpassword" className="form-control" value={this.state.passwordconf} onChange={(e)=>this.handleOnChange(e)} placeholder="Confirm Password..."></input>
                </div>
                <button className='btn btn-outline-primary' onClick={this.handleSubmit}>Sign Up</button>
            </div>
            </div>
        )
    }
}

export default SignUp
