import React from 'react';
import './App.css';
import CustomerIndex from './component/User-View/CustomerIndex';
import AdminIndex from './component/Admin-View/AdminIndex';
import jwt_decode from 'jwt-decode';
import {Link} from 'react-router-dom';
import PaymentIcon from '@material-ui/icons/Payment';
import ReceiptIcon from '@material-ui/icons/Receipt';
import LocalShippingIcon from '@material-ui/icons/LocalShipping';

import {BrowserRouter as Router,Redirect ,Switch, Route} from 'react-router-dom';
const adminAuth = sessionStorage.getItem("keytoken")
const PrivateRoute = ({component: Component, ...rest }) =>(
  <Route {...rest} render={(props) =>(
    (adminAuth !== null)
    ? (jwt_decode(adminAuth).role === "admin") ? <Component {...props} /> : <Redirect to='/home/login' />
    : <Redirect to='/home/login' />
  )} />
)
const PublicRoute = ({component: Component, ...rest }) =>(
  <Route {...rest} render={(props) =>(
    (adminAuth !== null)
    ? (jwt_decode(adminAuth).role !== "admin") ? <Component {...props} /> : <Redirect to='/admin' />
    : <Component {...props} />
  )} />
)


class App extends React.Component {
  componentDidMount(){
    this.setState()
  }
  render(){
  return (
    <Router>
      <img className="HeaderImg" src="https://eatpolska.com/wp-content/uploads/2017/10/new_fdt_header.jpg"alt='night-food' />
      <Switch>
        <PublicRoute path="/" exact component={CustomerIndex} />
        <PublicRoute path="/home" component={CustomerIndex}/>
        <PrivateRoute path="/admin" component={AdminIndex}/>
      </Switch>
      <div className="footer">
        <div className="row" style={{height:"10vw", }}>
          <div className="col-md-6 col-6" style={{padding:"2vw"}}>
            <p style={{margin:"0px", textAlign:"left"}}>1999-2019 © FoodWeb. All rights reserved</p>
            <p style={{margin:"0px", textAlign:"left"}}>Công ty TNHH FoodWeb</p>
            <p style={{margin:"0px", textAlign:"left"}}>xx Đường xx, Phường xx, Quận xx, TP.Hồ Chí Minh</p>
          </div>
          <div className="col-md-3 col-3" style={{padding:"2vw", fontSize:"1.2vw"}}>
            <Link to="/home/about">About Us</Link>
            <Link to="https://vnexpress.net/">Read Some News</Link>
            <Link to="https://edition.cnn.com/travel/article/world-best-foods-readers-choice/index.html">Some Best Restaurant</Link>
          </div>
          <div className="col-md-3 col-3" style={{padding:"2vw"}}>
            Payment:
            <br/>
            <img src="../../image/newcash.png" style={{width:"4vw", height:"3vw", verticalAlign:"0px"}} alt="cashpaylogo"/>
            <img src="../../image/visa.jpg" style={{width:"4vw", height:"3vw", verticalAlign:"0px"}} alt="visapaylogo"/>
            <img src="../../image/paypal.jpg" style={{width:"4vw", height:"3vw", verticalAlign:"0px"}} alt="masterpaylogo"/>
            <img src="../../image/master.jpg" style={{width:"4vw", height:"3vw", verticalAlign:"0px"}} alt="paypallogo"/>
          </div>
        </div>
      </div>
    </Router>
  );
  }
}

export default App;
